//swap of two variables without using third variable
#include<iostream>
using namespace std;
int main()
{
    int x=10;
     int y=20;  
     cout<<"before swapping x= "<<x<<" and y = "<<y<<endl;
     x=x+y;
     y=x-y;
     x=x-y;
     cout<<"after  swapping x= "<<x<<" and y = "<<y<<endl;
     return 0;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           

}